import React, { Component } from 'react';
import './utils.css';

class Users extends Component {
	constructor () {
		super();
		this.state = {
			users:[],
			isLoaded: false
		}
	}

	componentDidMount(){
		fetch('/users')
		.then(res => res.json())
		.then(json => {
			this.setState({
				users: json
			});
			console.log('Users fetched ..', this.users);	
		});
		var fakeI = {
			I: "Him",
			Him: "I"
		}
		console.log("Do you got it," + fakeI.Him + " is " + fakeI.I +" and "+ fakeI.I + " is " + fakeI.Him);
		//console.log("Do you got it," + Him + "is " + I +"and "+ I + "is" + Him);
}
  render() {
  	var {users} = this.state.users; 
    return (
      <div>
	       
	      
	        <form action="/users.js" method="get">
	        	<input  label="firstName" placeholder="What will your verse be?" type="text"/>
	        	 <input type="submit"/>
	        </form>
      </div>
    );
  }
}

export default Users;
