import React, { Component } from 'react';
import './utils.css';

class Users extends Component {
	constructor () {
		super();
		this.state = {
			users:[],
			isLoaded: false
		}
	}

	componentDidMount(){
		fetch('/users')
		.then(res => res.json())
		.then(json => {
			this.setState({
				isLoaded: true,
				users: json
			});
			console.log('Users fetched ..', this.state.users.users);	
		});
}
  render() {
  	var {isLoaded, users} = this.state; 
    
	if (!isLoaded){
       	return <div>Is loading ...</div>
       } else {
       	return(
       	<div>	
       		<ul>
       			{users.users.map(user => (
   					<li key={users.id}>
   						{user.userFirstName}
   					</li>	
       				))};
       			}
       		</ul>
       		<div>
		        <form action="/users.js" method="get">
		        	<input  label="firstName" placeholder="What will your verse be?" type="text"/>
		        	 <input type="submit"/>
		        </form>
	      	</div>
	      </div>
       	);
      }
  }
}
export default Users;
