import React, { Component } from 'react';
import './utils.css';

class Users extends Component {
	constructor () {
		super();
		this.state = {
			users:[],
			isLoaded: false
		}
	}

	componentDidMount(){
		fetch('/users')
		.then(res => res.json())
		.then(json => {
			this.setState({
				isLoaded: true,
				users: json
			});
			console.log('Users fetched ..', this.state.users);	
		});
		var fakeI = {
			I: "Him",
			Him: "I"
		}
		console.log("Do you got it," + fakeI.Him + " is " + fakeI.I +" and "+ fakeI.I + " is " + fakeI.Him);
}
  render() {
  	var {isLoaded, users} = this.state.users; 
    
    	if (!isLoaded){
	       	return <div>Is loading ...</div>
	       } else
	       {
	       	return(
		      <div>
			        <form action="/users.js" method="get">
			        	<input  label="firstName" placeholder="What will your verse be?" type="text"/>
			        	 <input type="submit"/>
			        </form>
		      </div>
	       
	       );
	       }
  }
}

export default Users;
