import React, { Component } from 'react';
import './utils.css';

class Users extends Component {
	constructor () {
		super();
		this.state = {
			users:[],
			products:[],
			isLoaded: false
		}
	}

	componentDidMount(){
		fetch('/api/users')
		.then(res => res.json())
		.then(json => {
			this.setState({
				isLoaded: true,
				users: json
			});
			console.log('Users fetched ..', this.state.users.users);	
		});
	fetch('/api/products')
		.then(res => res.json())
		.then(json => {
			this.setState({
				isLoaded: true,
				products: json
			});
			console.log('Products fetched ..', this.state.products.response.products);	
		});
}
  render() {
  	var {isLoaded, users} = this.state;
  	var products = this.state.products.response
    
	if (!isLoaded){
       	return <div>Loading ...</div>
       } else {
       	return(
       	<div>	
       		<div>
   				{users.users.map((user, index) => (
					<span key={index}>
						{user.userFirstName} {user.userLastName}
					</span>	
   				))}
       		</div>
       		<div>
   				{products.map((product, index) => (
					<span key={index}>
						{product.name} {product.price}
					</span>	
   				))}
       		</div>
       		<div>
		        <form action="/users.js" method="get">
		        	<input  label="firstName" placeholder="What will your verse be?" type="text"/>
		        	 <input type="submit"/>
		        </form>
	      	</div>
	      </div>
       	);
      }
  }
}
export default Users;
