import React, { Component } from 'react';
import './customers.css';

class Customers extends Component {
	constructor () {
		super();
		this.state = {
			customers:[]
		}
	}

	componentDidMount(){
		fetch('/api/customers')
		.then(res => res.json())
		.then(customers => this.setState({customers}, ()=> console.log('Customers fetched ..',this.state.customers, customers)));
	}
  render() {
    return (
      <div>
	      {this.state.customers.map(customer =>
	      		<span key={customer.id}> {customer.firstName} {customer.lastName}</span> 
	      	)}
	        <h2>The powerful play goes on, and you may contribute your verse</h2>
	        <form>
	        	<input action="/users.js" label="firstName" placeholder="What will your verse be?" type="text"/>
	        	 <input type="submit"/>
	        </form>
      </div>
    );
  }
}

export default Customers;
