import React, { Component } from 'react';

class Products extends Component {
	constructor () {
		super();
		this.state = {
			products:[],
			isLoaded: false
		}
	}

	componentDidMount(){
		fetch('/api/products')
		.then(res => res.json())
		.then(json => {
			this.setState({
				isLoaded: true,
				products: json
			});
			console.log('Products fetched ..', this.state.products.response.products[0].name);	
		});
}
  render() {
  	var {isLoaded, products} = this.state;
    
	if (!isLoaded){
       	return <div>Loading ...</div>
       } else {
       	return(
       	<div>	
       		<div>
   				{products.response.products.map((product, index) => (
					<span key={index}>
						{product.name} {product.price}
					</span>	
   				))}
       		</div>
       		<div>
		        <form action="/users.js" method="get">
		        	<input  label="firstName" placeholder="What will your verse be?" type="text"/>
		        	 <input type="submit"/>
		        </form>
	      	</div>
	      </div>
       	);
      }
  }
}
export default Products;
