import React, { Component } from 'react';

class PostForm extends Component {
	constructor (props) {
		super(props);
		this.state = {
			name:'',
			email:''
		};
		this.onChange = this.onChange.bind(this);
	}

	onChange(e){
		this.setState({[e.target.name]: e.target.value})
	}
	
  render() {
       	return(
       	<div>	
       		<div>
		        <form>
		        <label>Name</label><br/>
		        	<input  label="name" value={this.state.name} onChange={this.onChange} placeholder="What will your verse be?" type="text"/>
		        	<br/>
		        	<label>Email</label>
		        	<br/>
		        	<input label="email" value={this.state.email} onChange={this.onChange} placeholder="What will your verse be?" type="text"/>
		        	<br/>
		        	<input type="submit"/>
		        </form>
	      	</div>
	      </div>
       	);
      }
}
export default PostForm;
