import React, { Component } from 'react';
import './utils.css';

class Users extends Component {
	constructor () {
		super();
		this.state = {
			users:[]
		}
	}

	componentDidMount(){
		fetch('./users')
		.then(res => res.json())
		.then(json => {
			this.setState({
				users: json
			});
			console.log('Users fetched ..', this.users);	
		});
}
  render() {
  	var {users} = this.state; 
    return (
      <div>
	      {users.map(users =>
	      		<span key={users.id}> {users.firstName} {users.lastName}</span> 
	      	)}
	        
	        <form action="/users.js" method="get">
	        	<input  label="firstName" placeholder="What will your verse be?" type="text"/>
	        	 <input type="submit"/>
	        </form>
      </div>
    );
  }
}

export default Users;
